import { useEffect, useRef } from 'react';
import { CSSTransition } from 'react-transition-group';
import { gsap } from 'gsap';

const Index = (props) => {
    const mydiv = useRef(null);

    const effetDependencies = [mydiv];

    useEffect(() => {
        console.log(mydiv);
        gsap.from(mydiv.current, 1, {x: -100});
    }, effetDependencies)

    return(
        <CSSTransition
            timeout={1000}
            unmountOnExit
        >
            <div ref={mydiv}>My name is {props.name}</div>
        </CSSTransition>
    )
}


export default Index;
