import { useEffect, useRef } from 'react';
import { gsap } from 'gsap';

const Index = (props) => {
    const mydiv = useRef(null);

    const effetDependencies = [mydiv];

    useEffect(() => {
        gsap.from([mydiv], {duration:2, x:100});
    }, effetDependencies)

    return(
        <div ref={mydiv}>My name is {props.name}</div>
    )
}


export default Index;
