import React, { Component } from 'react';
import * as THREE from 'three';
import OrbitControls from 'three-orbitcontrols';
// import {MTLLoader, OBJLoader} from 'three-obj-mtl-loader';

import * as OBJLoader from 'three-obj-loader';
OBJLoader(THREE);


// var loader = new THREE.ObjectLoader();

// loader.load(
// 	// resource URL
// 	"models/json/example.json",

// 	// onLoad callback
// 	// Here the loaded data is assumed to be an object
// 	function ( obj ) {
// 		// Add the loaded object to the scene
// 		scene.add( obj );
// 	},

// 	// onProgress callback
// 	function ( xhr ) {
// 		console.log( (xhr.loaded / xhr.total * 100) + '% loaded' );
// 	},

// 	// onError callback
// 	function ( err ) {
// 		console.error( 'An error happened' );
// 	}
// );

class Scene extends Component {
    constructor(props){
        super(props);
        this.threecanvas = React.createRef();
        this.THREE = THREE;
    }
    componentDidMount(){
        // window.THREE = THREE; // THREE.NRRDLoader expects THREE to be a global object
        // require('three/examples/js/loaders/TextureLoader');

        var scene = new this.THREE.Scene();
        var camera = new this.THREE.PerspectiveCamera( 75, window.innerWidth/window.innerHeight, 0.1, 1000 );
        var renderer = new this.THREE.WebGLRenderer();
        renderer.setSize( window.innerWidth, window.innerHeight );
        this.threecanvas.current.appendChild( renderer.domElement );
        var geometry = new this.THREE.BoxGeometry( 1, 1, 1 );
        var cube;
        var object;
        var controls = new OrbitControls( camera, renderer.domElement );
        controls.target.set( 0, 0, 0 )

        var loader = new this.THREE.TextureLoader();

        // let mtlLoader = new MTLLoader();

        let objLoader = new this.THREE.OBJLoader();

        objLoader.load('/male02.obj',
            function ( obj ) {
                object = obj
                object.position.y = - 95;
                scene.add( object );
                animate();
            }

        );


        // objLoader.setMaterials(materials)
        // objLoader.load('/hand.obj', (object) => {
        //     scene.add(object)
        // })
        // mtlLoader.load('/hand.mtl', (materials) => {
        // materials.preload()
        // objLoader.setMaterials(materials)
        // objLoader.load('/hand.obj', (object) => {
        //     scene.add(object)
        // })
        // })

        loader.load('/logo.png', function ( texture ) {
                // in this example we create the material when the texture is loaded
                var material = new THREE.MeshBasicMaterial( {
                    map: texture
                 } );

                 cube = new THREE.Mesh( geometry, material );
                 scene.add( cube );

            }
        );

        // var loader = new THREE.ObjectLoader();
        // loader.load('/hand.obj',function ( object ) {
        //         scene.add( object );

        //     }
        // );

        camera.position.z = 5;
        var animate = function () {
          requestAnimationFrame( animate );
          cube.rotation.x += 0.01;
          cube.rotation.y += 0.01;
          object.translateZ( -10 );
          renderer.render( scene, camera );
        };

    }
    render(){
        return (
            <div id="three-container" ref={this.threecanvas} />
        )
    }
}

export default Scene;
