import React, { Component, useEffect, useRef } from 'react';
import { CSSTransition } from "react-transition-group";
import { gsap } from 'gsap';

class Index extends Component {
    constructor(props){
        super(props);
        this.mydiv = React.createRef();

        this.effetDependencies = [this.mydiv];
    }

    onEnter(){
        console.log('entering')
        gsap.from([this.mydiv.current], 1,{x:-100});
    }

    // componentWillUnmount() {
    //     gsap.from([this.mydiv.current], 1,{x:100});
    // }

    render(){
        return(
            <CSSTransition
            timeout={1000}
            mountOnEnter
            unmountOnExit
            enter={true}
            onEnter={this.onEnter}
            onEntered={()=>{console.log('in ---')}}
            in={true}
            addEndListener={()=> {
                gsap.from([this.mydiv.current], 1,{x:-100});
            }}
          >
              <div ref={this.mydiv}>My name is {this.props.name}</div>
          </CSSTransition>
        )
    }
}


export default Index;
