import { Component, useEffect, useRef } from 'react';
import { TimelineMax } from 'gsap';

class Index extends Component {
    constructor(props){
        super(props);
        this.mydiv = useRef(null);
        tl = new TimelineMax();

        effetDependencies = [mydiv];
    }

    componentDidMount(){
        useEffect(() => {
            //Enter animation
            tl.from([mydiv.current], 1,
                {
                    x:-100
                });

        }, effetDependencies);
    }

    componentWillUnmount() {
        console.log('unmounting')
    }

    render(){
        return(
            <div ref={mydiv}>My name is {thisprops.name}</div>
        )
    }
}


export default Index;
