import React, { Component, useEffect, useRef } from 'react';
import { gsap } from 'gsap';

class Index extends Component {
    constructor(props){
        super(props);
        this.mydiv = React.createRef();

        this.effetDependencies = [this.mydiv];
    }

    componentDidMount(){
        gsap.from([this.mydiv.current], 1,{x:-100});
    }

    componentWillUnmount() {
        gsap.from([this.mydiv.current], 1,{x:100});
    }

    render(){
        return(
            <div ref={this.mydiv}>My name is {this.props.name}</div>
        )
    }
}


export default Index;
