import { useEffect, useRef } from 'react';
import { TimelineMax } from 'gsap';

const Index = (props) => {
    const mydiv = useRef(null);
    const tl = new TimelineMax();

    const effetDependencies = [mydiv];

    useEffect(() => {
        //Enter animation
        tl.from([mydiv.current], 1,
            {
                x:-100
            });

    }, effetDependencies)

    return(
        <div ref={mydiv}>My name is {props.name}</div>
    )
}


export default Index;
