import { useRouter } from "next/router";

const Navbar = () => (
    <nav>
        <ul>
            <li className={router.pathname == "/" ? "active" : ""}>
                <Link href="/"><a>home</a></Link>
            </li>
            <li className={router.pathname == "/about" ? "active" : ""}>
                <Link href="/about"><a>about</a></Link>
            </li>
        </ul>
    </nav>
);

export default Navbar;
