import React, { Component, useEffect, useRef } from 'react';
import { Transition } from "react-transition-group";
import { gsap } from 'gsap';

class Index extends Component {
    constructor(props){
        super(props);
        this.mydiv = React.createRef();

        this.effetDependencies = [this.mydiv];
    }

    // componentDidMount(){
    //     gsap.from([this.mydiv.current], 1,{x:-100});
    // }

    // componentWillUnmount() {
    //     gsap.from([this.mydiv.current], 1,{x:100});
    // }

    render(){
        return(
            <Transition
            timeout={1000}
            mountOnEnter
            unmountOnExit
            in={true}
            addEndListener={(node, done) => {
              gsap.to(nthis.mydiv, 0.5, {
                x: 100,
                onComplete: done
              });
            }}
          >
              <div ref={this.mydiv}>My name is {this.props.name}</div>
          </Transition>
        )
    }
}


export default Index;
